import React, { useState } from "react";

import {
  Card,
  Row,
  Col,
  Typography,
  Input,
  Form,
  Radio,
  // Select,
  // Tooltip,
  // DatePicker,
  // Button,
  // Table,
} from "antd";

//Card title
const { Title } = Typography;

//from on finish
const onFinish = (values) => {
  console.log("Received values of form: ", values);
};
const onFinishFailed = (errorInfo) => {
  console.log("Failed:", errorInfo);
};

//mobile number prefixse

function App() {
  return (
    <div className="App">
      <>
        <Row justify="center">
          <Col>
            <Card style={{ width: "100VW", height: 100 }}>
              <Title level={1}>Register Form</Title>
            </Card>
          </Col>
        </Row>
        <br />
        <br />
        <Row justify="center">
          <Col span={24}>
            <Form
              name="basic"
              labelCol={{
                span: 10,
              }}
              wrapperCol={{
                span: 6,
              }}
              initialValues={{
                remember: true,
              }}
              onFinish={onFinish}
              onFinishFailed={onFinishFailed}
              autoComplete="off"
            >
              <Form.Item
                label="First Name"
                name="First Name"
                rules={[
                  { required: true, message: "Please input your first name!" },
                ]}
              >
                <Input placeholder="Enter your First Name" />
              </Form.Item>
              <Form.Item
                label="First Name"
                name="First Name"
                rules={[
                  { required: true, message: "Please input your second name!" },
                ]}
              >
                <Input placeholder="Enter your second Name" />
              </Form.Item>
              <Form.Item
                label="Sex"
                name="Sex"
                rules={[{ required: true, message: "Please select your Sex!" }]}
              >
                <Radio.Group name="Sex">
                  <Radio value={"Male"}>Male</Radio>
                  <Radio value={"Female"}>Female</Radio>
                  <Radio value={"other"}>Other</Radio>
                </Radio.Group>
              </Form.Item>
              <Form.Item
                name="phone"
                label="Phone Number"
                rules={[
                  {
                    required: true,
                    message: "Please input your phone number!",
                  },
                ]}
              >
                <Input
                  addonBefore={prefixSelector}
                  style={{
                    width: "100%",
                  }}
                />
              </Form.Item>
            </Form>
          </Col>
        </Row>
      </>
    </div>
  );
}

export default App;
