import React, { useState } from "react";

import {
  Card,
  Row,
  Col,
  Typography,
  Input,
  Form,
  Radio,
  Select,
  // Tooltip,
  // DatePicker,
  // Button,
  // Table,
} from "antd";

//Card title
const { Title } = Typography;

//from on finish
const onFinish = (values) => {
  console.log("Received values of form: ", values);
};
const onFinishFailed = (errorInfo) => {
  console.log("Failed:", errorInfo);
};

//mobile number prefixsector
const { Option } = Select;
const prefixSelector = (
  <Form.Item name="prefix" noStyle>
    <Select
      style={{
        width: 70,
      }}
    >
      <Option value="+91">+91</Option>
      <Option value="+87">+87</Option>
    </Select>
  </Form.Item>
);

//for email validation
const validateMessages = {
  required: '$label is required!',
  types: {
    email: '$label is not a valid email!',
  },
};

// text area
const { TextArea } = Input;


function App() {
  return (
    <div className="App">
      <>
        <Row justify="center">
          <Col>
            <Card style={{ width: "100VW", height: 100 }}>
              <Title level={1}>Register Form</Title>
            </Card>
          </Col>
        </Row>
        <br />
        <br />
        <Row justify="center">
          <Col span={24}>
            <Form
              name="basic"
              labelCol={{
                span: 10,
              }}
              wrapperCol={{
                span: 6,
              }}
              initialValues={{
                remember: true,
              }}
              onFinish={onFinish}
              onFinishFailed={onFinishFailed}
              // for email validation
              validateMessages={validateMessages}
              autoComplete="off"
            >
              <Form.Item
                label="First Name"
                name="First Name"
                rules={[
                  { required: true, message: "Please input your first name!" },
                ]}
              >
                <Input placeholder="Enter your First Name" />
              </Form.Item>
              <Form.Item
                label="First Name"
                name="First Name"
                rules={[
                  { required: true, message: "Please input your second name!" },
                ]}
              >
                <Input placeholder="Enter your second Name" />
              </Form.Item>
              <Form.Item
                label="Sex"
                name="Sex"
                rules={[{ required: true, message: "Please select your Sex!" }]}
              >
                <Radio.Group name="Sex">
                  <Radio value={"Male"}>Male</Radio>
                  <Radio value={"Female"}>Female</Radio>
                  <Radio value={"other"}>Other</Radio>
                </Radio.Group>
              </Form.Item>
              <Form.Item
                name="phone"
                label="Phone Number"
                rules={[
                  {
                    required: true,
                    message: "Please input your phone number!",
                  },
                ]}
              >
                <Input
                  addonBefore={prefixSelector}
                  maxLength={10}
                  style={{
                    width: "100%",
                  }}
                  placeholder="Enter your mobile number"
                />
              </Form.Item>
              <Form.Item
                name={["user", "email"]}
                label="Email"
                rules={[
                  {
                    type: "email",
                  },
                ]}
              >
                <Input placeholder="Enter your email id"/>
              </Form.Item>
              <Form.Item
                label="Address"
                name="address"
                rules={[
                  { required: true, message: "Please input your Address!" },
                ]}

              >
                 <TextArea showCount maxLength={50} style={{ height: 100 }} placeholder="Enter your address"/>
              </Form.Item>
              <Form.Item
                label="Date"
                name="Date"
                rules={[
                  {
                    required: true,
                    message: "Please select your date of birth!",
                  },
                ]} 
              >
                <DatePicker />
              </Form.Item>
            </Form>
          </Col>
        </Row>
      </>
    </div>
  );
}

export default App;
