import React, { useState } from "react";

import {
  Card,
  Row,
  Col,
  Typography,
  Input,
  Form,
  Radio,
  Select,
  DatePicker,
  // Button,
  // Table,
} from "antd";

//Card title
const { Title } = Typography;

//from on finish
const onFinish = (values) => {
  console.log("Received values of form: ", values);
};
const onFinishFailed = (errorInfo) => {
  console.log("Failed:", errorInfo);
};

//mobile number prefixsector
const { Option } = Select;
const prefixSelector = (
  <Form.Item name="prefix" noStyle>
    <Select
      style={{
        width: 70,
      }}
    >
      <Option value="+91">+91</Option>
      <Option value="+87">+87</Option>
    </Select>
  </Form.Item>
);

//for email validation
const validateMessages = {
  required: "$label is required!",
  types: {
    email: "$label is not a valid email!",
  },
};

// text area
const { TextArea } = Input;

// qualifiction
// const filteredOptions = OPTIONS.filter((o) => !qualification.includes(o));
// const OPTIONS = ["10th", "12th", "diplamo", "B.E", "B.sc", "B.com"];



function App() {
  const [FormData,setFormData] = useState({
    fname:"",
    sname:"",
    sex:"",
    mobilenumber:"",
    emailid:"",
    address:"",
    dob:"",
    status:"",
  });
  function handleFileds(event){
    const namField=event.target.name
    const value = event.target.value
    setFormData((prevalue)=>({...prevalue,[namField]:value}))
  }
  function handleFiledss(date, dateString){
    const namField= target.name
    const value = {date,dateString}
  }
  return (
    <div className="App">
      <>
        <Row justify="center">
          <Col>
            <Card style={{ width: "100VW", height: 100 }}>
              <Title level={1}>Register Form</Title>
            </Card>
          </Col>
        </Row>
        <br />
        <br />
        <Row justify="center">
          <Col span={24}>
            <Form
              name="basic"
              labelCol={{
                span: 10,
              }}
              wrapperCol={{
                span: 6,
              }}
              initialValues={{
                remember: true,
              }}
              onFinish={onFinish}
              onFinishFailed={onFinishFailed}
              // for email validation
              validateMessages={validateMessages}
              autoComplete="off"
            >
              <Form.Item
                label="FirstName"
                rules={[
                  { required: true, message: "Please input your first name!" },
                ]}
              >
                <Input name="fname" placeholder="Enter your First Name" onChange={handleFileds}  />
              </Form.Item>
              <Form.Item
                label="secondName"
                rules={[
                  { required: true, message: "Please input your second name!" },
                ]}
              >
                <Input name="sname" placeholder="Enter your second Name" onChange={handleFileds} />
              </Form.Item>
              <Form.Item
                label="Sex"
                rules={[{ required: true, message: "Please select your Sex!" }]}
              >
                <Radio.Group name="Sex" onChange={handleFileds}>
                  <Radio value={"Male"}>Male</Radio>
                  <Radio value={"Female"}>Female</Radio>
                  <Radio value={"other"}>Other</Radio>
                </Radio.Group>
              </Form.Item>
              <Form.Item

                label="Phone Number"
                rules={[
                  {
                    required: true,
                    message: "Please input your phone number!",
                  },
                ]}

              >
                <Input
                  name="mobilenumber"
                  onChange={handleFileds}
                  addonBefore={prefixSelector}
                  maxLength={10}
                  style={{
                    width: "100%",
                  }}
                  placeholder="Enter your mobile number"
                />
              </Form.Item>
              <Form.Item
                name={["user", "email"]}
                label="Email"
                rules={[
                  {
                    type: "email",
                  },
                ]}
              >
                <Input name="emaild" placeholder="Enter your email id" onChange={handleFileds} />
              </Form.Item>
              <Form.Item
                label="Address"
                rules={[
                  { required: true, message: "Please input your Address!" },
                ]}
              >
                <TextArea
                  name="address"
                  showCount
                  maxLength={50}
                  style={{ height: 100 }}
                  placeholder="Enter your address"
                  onChange={handleFileds}
                />
              </Form.Item>
              <Form.Item
                label="Date of birth"
                name="Date"
                rules={[
                  {
                    required: true,
                    message: "Please select your date of birth!",
                  },
                ]}
              >
                <DatePicker
                  name="dob"
                  format="DD/MM/YYYY"
                  placeholder="select your date of birth"

                  onSelect={handleFiledss}
                />
              </Form.Item>
              <Form.Item
                label="Status"

                rules={[
                  {
                    required: true,
                    message: "Please select your status!",
                  },
                ]}
              >
                <Select
                name="status"
                onChange={handleFileds}
                  style={{
                    width: 120,
                  }}
                  placeholder="select your status"
                  options={[
                    {
                      value: "single",
                      label: "single",
                    },
                    {
                      value: "married",
                      label: "married",
                    },
                  ]}
                />
              </Form.Item>
              {/* <Form.Item
                label="Qualification"
                name="Qualification"
                rules={[
                  {
                    required: true,
                    message: "Please select your qualification!",
                  },
                ]}
              >
                <Select
                  mode="multiple"
                  placeholder="select your qualification"
                  style={{
                    width: "100%",
                  }}
                  options={filteredOptions.map((item) => ({
                    value: item,
                    label: item,
                  }))}
                />
              </Form.Item> */}
                  {
                      JSON.stringify(FormData)
                  }
            </Form>
          </Col>
        </Row>

      </>

    </div>

  );
}

export default App;
